import './App.css';


//app is known as component
function App() {

  return (
    <div className="App">
        <User name="Tlotliso" age={21} email="arthur@gmail.com"/>
        <User name="Tlo" age={25} email="tlou@gmail.com"/>
        <User name="Kagiso" age={20} email="kagiso@gmail.com"/>
      </div>
  );

}

// we should use the props
//components always start with capital letters, but js can start a method with small letters

const User = (props) =>{
  return (
    <div>
      <h1>{props.name}</h1>
      <h1>{props.age}</h1>
      <h1>{props.email}</h1>
    </div>
  )
};

export default App;
